def gauss(f, a, b, E, A, n):
    y = [None] * (n+1)
    for i in range(0, n+1):
        y[i] = ((b-a)/2)*E[i] + (a+b)/2
    sum = 0
    for i in range(0, n+1):
        sum = sum + A[i]*f(y[i])
    sum = sum *((b-a)/2)
    return sum, abs(math.pi - sum)
def f(x):
    return 4/(1+x**2)


